import sys, json
variables = json.loads(sys.argv[1])['variables']

output_variables = {}
my_list = []
for variable in variables:
        if variable['properties']['scope'] == 'output':
            d={}
            d['name'] = variable['properties']['name']
            d['value'] = variable['properties']['value']
            my_list.append(d)
output_variables = json.dumps(my_list)
